import React, { useEffect, useState, useRef } from 'react';
import './App.css';

const BACKEND_URL = 'https://f27216ce1d16.ngrok-free.app'; // Change to ngrok URL for remote play

function App() {
  const [gameId, setGameId] = useState<string>('');
  const [inputId, setInputId] = useState<string>('');
  const [player, setPlayer] = useState<'Player 1' | 'Player 2' | ''>('');
  const [game, setGame] = useState<any>(null);
  const [selected, setSelected] = useState<[number, number][]>([]);
  const [error, setError] = useState<string | null>(null);
  const pollingRef = useRef<NodeJS.Timeout | null>(null);

  // Poll for game state
  useEffect(() => {
    if (!gameId) return;
    const poll = () => {
      fetch(`${BACKEND_URL}/api/game/${gameId}`)
        .then(res => res.json())
        .then(setGame)
        .catch(() => setError('Failed to fetch game state'));
    };
    poll();
    pollingRef.current = setInterval(poll, 2000);
    return () => { if (pollingRef.current) clearInterval(pollingRef.current); };
  }, [gameId]);

  // Create a new game
  const handleCreate = async () => {
    const res = await fetch(`${BACKEND_URL}/api/game`, { method: 'POST' });
    const data = await res.json();
    setGameId(data.id);
    setPlayer('Player 1');
    setGame(data);
    setSelected([]);
    setError(null);
  };

  // Join an existing game
  const handleJoin = async () => {
    const res = await fetch(`${BACKEND_URL}/api/game/${inputId}`);
    if (res.ok) {
      const data = await res.json();
      setGameId(inputId);
      setPlayer('Player 2');
      setGame(data);
      setSelected([]);
      setError(null);
    } else {
      setError('Game not found');
    }
  };

  // Handle cell selection
  const handleCellClick = (i: number, j: number) => {
    if (!game || game.turn !== player) return;
    const idx = selected.findIndex(([x, y]) => x === i && y === j);
    let newSelected: [number, number][];
    if (idx !== -1) {
      newSelected = selected.slice(0, idx).concat(selected.slice(idx + 1));
    } else {
      newSelected = [...selected, [i, j]];
    }
    setSelected(newSelected);
    setError(null);
  };

  // Submit move
  const handleSubmit = async () => {
    if (!game || game.turn !== player) return;
    if (selected.length < 2) {
      setError('Select at least 2 letters.');
      return;
    }
    const word = selected.map(([i, j]) => game.board[i][j]).join('');
    const res = await fetch(`${BACKEND_URL}/api/game/${gameId}/move`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ word, positions: selected, player })
    });
    const data = await res.json();
    setGame(data);
    setSelected([]);
    setError(null);
  };

  if (!gameId) {
    return (
      <div className="App">
        <h1>Word Strategy Game Multiplayer</h1>
        <button onClick={handleCreate}>Create New Game</button>
        <div style={{ margin: 10 }}>or</div>
        <input value={inputId} onChange={e => setInputId(e.target.value)} placeholder="Enter Game ID" />
        <button onClick={handleJoin}>Join Game</button>
        {error && <div style={{ color: 'red' }}>{error}</div>}
      </div>
    );
  }

  return (
    <div className="App">
      <h1>Word Strategy Game Multiplayer</h1>
      <div>Game ID: <b>{gameId}</b></div>
      <div>You are: <b>{player}</b></div>
      <div>Current turn: <b>{game?.turn}</b></div>
      {game?.board && (
        <div style={{ display: 'inline-block', border: '2px solid #333', marginTop: 20 }}>
          {game.board.map((row: string[], i: number) => (
            <div key={i} style={{ display: 'flex' }}>
              {row.map((cell: string, j: number) => {
                const isSelected = selected.some(([x, y]) => x === i && y === j);
                return (
                  <div
                    key={j}
                    onClick={() => handleCellClick(i, j)}
                    style={{
                      width: 28,
                      height: 28,
                      border: '1px solid #aaa',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontWeight: 'bold',
                      fontSize: 18,
                      background: isSelected ? '#90ee90' : '#fff',
                      cursor: game.turn === player ? 'pointer' : 'not-allowed',
                      userSelect: 'none',
                    }}
                  >
                    {cell}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
      )}
      <div style={{ marginTop: 20 }}>
        <strong>Selected Word:</strong> {selected.map(([i, j]) => game.board[i][j]).join('')}
      </div>
      <button onClick={handleSubmit} disabled={selected.length === 0 || game.turn !== player} style={{ marginTop: 10 }}>
        Submit Word
      </button>
      {error && <div style={{ color: 'red', marginTop: 10 }}>{error}</div>}
      <div style={{ marginTop: 30, textAlign: 'left', maxWidth: 600, marginLeft: 'auto', marginRight: 'auto' }}>
        <h2>Move History</h2>
        <ol>
          {game?.moves?.map((move: any, idx: number) => (
            <li key={idx}>
              <strong>{move.player}:</strong> {move.word}
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default App;
